
public class Multiplicador {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cont,numero=5;
		for(cont=0;cont<=10;cont++){
			System.out.print (cont);
			System.out.print (" x ");
			System.out.print (numero);
			System.out.print (" = ");
			System.out.println (cont*numero);
		}
	}
}
